mapper.update:
UPDATE  Employee
SET     password=sha2(concat(#{password}, #{salt}), 256),

            email=#{email},


            recommendFlag=#{recommendFlag},

        salt=#{salt}
WHERE   id = #{empNo}

mapper.select:
SELECT  count(id)
FROM    Employee
WHERE   emp_no = #{empNo}
  AND   email = #{email}

mapper.update:
UPDATE  Employee
SET     password=sha2(concat(#{password}, #{salt}), 256), salt=#{salt}
WHERE   emp_no = #{empNo}
