mapper.delete:
DELETE
FROM favoriteUser
WHERE id = #{id}
  AND followId = #{followId}

mapper.select:
SELECT followId
FROM favoriteUser
WHERE id = #{id}

mapper.delete:
DELETE
FROM favoritePicture
WHERE id = #{id}
  AND pictureIdx = #{pictureIdx}

mapper.select:
SELECT count(*)
FROM favoritePicture
WHERE id = #{id}
