mapper.select:
SELECT count(id)
FROM userDTO
WHERE id = #{id}

mapper.update:
UPDATE userDTO
SET password=sha2(concat(#{password}, #{salt}), 256),

    email=#{email},


    recommendFlag=#{recommendFlag},

salt=#{salt}
WHERE id = #{id}

mapper.select:
SELECT count(id)
FROM userDTO
WHERE id = #{id}
  AND email = #{email}

mapper.update:
UPDATE userDTO
SET password=sha2(concat(#{password}, #{salt}), 256),
    salt=#{salt}
WHERE id = #{id}

mapper.delete:
delete
from interest
where id = #{id}
  and dongCode = #{dongcode}
